"use strict";window.onload=function(){$.get("../interface/showlist.php",function(c){var n=JSON.parse(c);if(1==n.code){$(".toshop_box").css({display:"none"}),$(".product_list").css({display:"block"});for(var t="",d=0;d<n.data.length;d++){t='\n                <div class="product_item">\n                            <div class="product_check">\n                                <input type="checkbox" name="ck1" id="check1">\n                            </div>\n                            <div class="product_img">\n                                <img src="'.concat(n.data[d].product_img,'" alt="">\n                            </div>\n                            <div class="product_name">\n                                ').concat(n.data[d].product_name,'\n                            </div>\n                            <div class="product_one_price">').concat(n.data[d].product_price,'</div>\n                            <div class="product_number"> \n                                <span>-</span><strong>').concat(n.data[d].product_num,'</strong><em>+</em>\n                            </div>\n                            <div class="product_sum_price">\n                                ').concat(n.data[d].product_price,'\n                            </div>\n                            <div class="product_operation">\n                                    删除\n                            </div>\n                </div>               \n                ');var a=document.createElement("div");a.className="product_item",a.innerHTML=t,document.getElementsByClassName("product_list_main")[0].appendChild(a)}}else console.log("请求数据失败")})};